mapper.select:
		SELECT * FROM(SELECT ROW_NUMBER()OVER(


				    ORDER BY D.DISNAME ASC


				    ORDER BY D.DISNAME DESC


				    ORDER BY D.DISNO ASC


				    ORDER BY D.DISNO DESC


				    ORDER BY D.DISNO DESC


            ) AS RNUM,
            D.DISNO, D.PETTYPEID, 
	                    D.DISNAME, D.DEFINITION, D.CAUSE, 
	                    D.SYMPTOM, D.TREATMENT, D.TIP, 
                    D.CREATEDAT, D.UPDATEDAT
				FROM PETDISEASE D
				JOIN PETTYPE P ON D.PETTYPEID=P.PETTYPEID




			        D.PETTYPEID = #{search}


			        D.DISNAME like #{search}


			     D.PETTYPEID = #{search}
			     OR  D.DISNAME like #{search}



		) A  
		where  A.rnum  between  #{start}  and #{end}

    SELECT COUNT(*)
			FROM PETDISEASE D
			JOIN PETTYPE P ON D.PETTYPEID=P.PETTYPEID



		      D.PETTYPEID = #{search}


		      D.DISNAME LIKE #{search}


		      (D.PETTYPEID = #{search} OR D.DISNAME LIKE #{search})
